mapper.update:
UPDATE member
   SET userName = #{userName},
       email = #{email},
       phone = #{phone}
 WHERE userId = #{userId}

mapper.insert:
INSERT INTO member_auth
	(
	userId,
	auth
	)
VALUES
	(
	#{userId},
	#{auth}
	)

mapper.select:
SELECT userId
  FROM member
 WHERE userId = #{userId}

SELECT userId
  FROM member
 WHERE email = #{email}

mapper.update:
UPDATE member
   SET userPw = #{userPw}
 WHERE userId = #{userId}
